import * as React from 'react';
import { Button } from '@material-ui/core';
import { inject, observer } from 'mobx-react';
import { Redirect } from 'react-router-dom';
import { IUserStore } from '../stores/UserStore';
import { AppRoutes } from './AppRoutes';
import './App.css';

interface IAppTabsProps {
    userId: string;
    loggedIn: boolean;
    userStore?: IUserStore;
}

@inject('userStore')
@observer
export default class AppTabs extends React.Component<IAppTabsProps> {
    public render() {
        if (!this.props.loggedIn) {
            return <Redirect to={AppRoutes.loginRoute} />;
        }

        return (
           
            <div className="r-private-content">
                <h1>Private content</h1>
                <h2>Welcome, {window.authContext.email}</h2>
                <Button variant="outlined" onClick={async (): Promise<void> => await this.props.userStore.handleLogout()}>
                    Log out
                </Button>
                <Button variant="outlined" onClick={async (): Promise<void> => await this.props.userStore.handleChange()}>
                   Edit
                </Button>
                <Card variant="outlined" className="r-auth-form">
                <CardContent>
                    <h2>Create an account</h2>
                    <Grid container spacing={1} alignItems="center">
                        <Grid container item xs={12}>
                            <TextField label="Email" variant="outlined" onChange={(event) => this.setEmail(event?.target?.value)} />
                        </Grid>
                        <Grid container item xs={12}>
                            <TextField
                                label="Password"
                                variant="outlined"
                                type="password"
                                onChange={(event) => this.setPassword(event?.target?.value)}
                            />
                        </Grid>
                        <Grid container item xs={12}>
                            <TextField
                                label="Confirm Password"
                                variant="outlined"
                                type="password"
                                onChange={(event) => this.setPasswordMatch(event?.target?.value)}
                            />
                        </Grid>
                        <Grid container item xs={6}>
                            <Button
                                variant="outlined"
                                color="primary"
                                onClick={async (): Promise<void> => await this.handleRegister()}
                                disabled={this.setCorrectStateSaveButton()}
                            >
                                Register
                            </Button>
                        </Grid>
                        <Grid container item xs={6}>
                            <Button component={Link} to={AppRoutes.loginRoute} color="secondary">
                                Login
                            </Button>
                        </Grid>
                    </Grid>
                </CardContent>
            </Card>
            </div>
        );
    }
}
